effect.shader:

struct VertexPosHTex
{
    float4 posH : SV_POSITION;
    float2 tex : TEXCOORD;
};

struct VertexPosTex
{
    float3 posL : POSITION;
    float2 tex : TEXCOORD;
};

// 顶点着色器(2D)
VertexPosHTex VS(VertexPosTex vIn)
{
    VertexPosHTex vOut;
    vOut.posH = float4(vIn.posL, 1.0f);
    vOut.tex = vIn.tex;
    return vOut;
}

// 像素着色器(2D)
float4 PS(VertexPosHTex pIn) : SV_Target
{
    float4 color = src1_tex.Sample(point_sampler, pIn.tex);
    clip(color.a - 0.1f);
    return color;
}
